actions.action:
printf "{@name}: " > /tmp/stat.txt;
echo "%tw_filename1%" >> /tmp/stat.txt;
printf "{@name=Name} ({@real}): " >> /tmp/stat.txt;
realpath "%tw_filename1%" >> /tmp/stat.txt;
printf "{@part_size}: " >> /tmp/stat.txt;
du -h "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /tmp/stat.txt;
stat "%tw_filename1%" | grep Access | sed 's/Access/{@access}/g' 2>/dev/null 1>> /tmp/stat.txt;
stat "%tw_filename1%" | grep Modify | sed 's/Modify/{@time_m}/g' 2>/dev/null 1>> /tmp/stat.txt;
stat "%tw_filename1%" | grep Change | sed 's/Change/{@time_c}/g' 2>/dev/null 1>> /tmp/stat.txt;

echo "MD5:" > /tmp/stat.txt;
md5sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /tmp/stat.txt;
echo "SHA1:" >> /tmp/stat.txt;
sha1sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /tmp/stat.txt;
echo "SHA3:" >> /tmp/stat.txt;
sha3sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /tmp/stat.txt;
echo "SHA256:" >> /tmp/stat.txt;
sha256sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /tmp/stat.txt;
echo "SHA512:" >> /tmp/stat.txt;
sha512sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /tmp/stat.txt;
echo "SUM:" >> /tmp/stat.txt;
sum "%tw_filename1%" >> /tmp/stat.txt;
